INSERT INTO `titluri_repertoriu_canonic` (`id`, `nume`, `continut`) VALUES
(1, 'TITLUL PRIM:', 'Despre învăţătura asupra lui Dumnezeu, despre credința ortodoxă despre canoane şi despre hirotonii. \r\n'),
(2, 'TITLUL AL DOILEA:', 'Despre clădirea bisericilor, despre sfinţirea bisericilor, şi despre bisericile martirilor, care nu au moaşte de-ale sfinţilor, despre vasele sfinţite şi despre lucrurile afierosite şi despre cleric'),
(3, 'TITLUL AL TREILEA:', 'Despre rugăciuni, despre cântare, citire, aducerea Jertfei, cuminecare, despre veşmintele şi slujba citeţilor, cântăreţilor şi servilor. \r\n'),
(4, 'TITLUL AL PATRULEA:', ' Despre catehumeni şi despre sfântui Botez \r'),
(5, 'TITLUL AL CINCILEA:', ' Despre cei ce dispreţuiesc bisericile şi adunările din biserică, şi pomenirea sfinţilor şi despre cei ce mănâncă în biserici şi despre agape \r'),
(6, 'TITLUL AL SASELEA:', ' Despre aducerea produselor pentru Jertfă \r'),
(7, 'TITLUL AL ŞAPTELEA:', ' Despre post şi despre Patruzecime, despre Paşti, Cincizecime, despre Duminică şi sâmbătă şi despre îngenunchere \r'),
(8, 'TITLUL AL OPTULEA:', ' Despre parohii, şi de ce să petreacă episcopii şi clericii într-un loc şi despre mutarea lor într-un alt loc; despre sinoadele anuale, despre primirea străinilor, despre învăţătură, despre scrisorile'),
(9, 'TITLUL AL NOUĂLEA:', ' Despre delictele şi judecarea episcopilor, de-spre afurisire, caterisire, penitenţă şi care delicte se dezleagă prin punerea mâinilor \r'),
(10, 'TITLUL AL ZECELEA:', ' Despre administrarea bunurilor bisericesti, despre proprietatea episcopului \r\n'),
(11, 'TITLUL AL UNSPREZECELEA:', ' Despre mănăstiri si monahi \r'),
(12, 'TITLUL AL DOISPREZECELEA:', ' Despre eretici, iudei si păgâni \r'),
(13, 'TITLUL AL TREISPREZECELEA:', ' Despre laici \r'),
(14, 'TITLUL AL PATRUSPREZECELEA:', ' Despre lucrurile comune tuturor oamenilor \r');
